package com.syntaxBatch11Class01;

public class VariablesPractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
         //primitive data Types
		
		// Numeric  Types
		    
		byte section1= 127;
		
		short section2=9990;
		
		int section3=999991009;
		
		long section4=10000000000000000L;
		
	           // Decimal Numbers
		
	    float a=1.40880f;
		    
	     double b=89.99000;
		  
		  // Non Decimal Numbers( single Characters
		  
		  char character='$';
		
		  char letter='K';
	
		  
		  // Representing Boolean Values( Yes or No)
		  
		  boolean Beautiful=true;  // Yes
		   
		  boolean boring=false;    //  No
		
		  
		  System.out.println(section1);
		  System.out.println(section2);
		  System.out.println(section3);
		  System.out.println(section4);
		  System.out.println( a);
		  System.out.println(b);
		  System.out.println(character);
		  System.out.println(letter);
		  System.out.println(Beautiful);
		  System.out.println(boring);
		  
		  
		
	} 

}
