package com.syntaxBatch11Class02;

public class ModOperatorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number1=30;
		
		int number2=4;
		
		System.out.println(number1%number2);
		
		int number3=29;
		int number4=7;
		
		System.out.println(number3%number4);
		
		
		int number5=8;
		int number6=2;
		
		System.out.println(number5%number6);
		
		
		int number7=13;
		int number8=5;
		
		System.out.println(number7%number8);
		
		System.out.println(3%5); // if I'm taking mod of smaller number with a bigger number I will get smaller number as the answer.
		
		
		System.out.println(number7/number8); // In this part I get the division part.
		
		System.out.println(number7%number8); // In this part I get the Mod or remainders.
		
		
		double number9=13;
		double number10=5;
		// On double value  I get the result as decimal number.
		System.out.println(number9/number10);
		
		System.out.println(number9%number10);
    
	}

}
