package com.syntaxBatch11Class1;

public class PrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Representing and storing the whole numbers
		byte box1=127;//rang from -128 till 127
		
		short box2=30000;//32768 to 32767
		
		int box3=2000000000;// most popular data type to use for whole numbers
		
		long box4=99000000000L; // for long numbers
		
		//Representing and storing the whole decimal numbers
		
		float a=1.99f;
		
		double b=19.99;// most popular data types for decimal numbers
		
		// Representing and storing single character 
		
		char character='#';
		
		char letter='c';
		
		
		// representing and storing boolean values(yes or no)
		
		boolean hungry=true;// yes
		
		boolean tired=false;// no
		
		System.out.println(box1);
		
		System.out.println("box1");
		
		System.out.println(hungry);
		System.out.println(letter);
		
		
	
	}

}
