package com.syntaxBatch11Class10;

public class EnhanceForLoopDemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		String [] cars= {" Jeep", "BMW",  "Toyota"};
		
		for(String st:cars) {
			
			System.out.print(st+" "); 
		}
		System.out.println();
		System.out.println("**************");
		
		char [] grades= {'A', 'B', 'C' , 'D'};
		
		for(char ch:grades) {
			
			System.out.println(ch+" ");  
		}
	}

}
