package com.syntaxBatch11Class2;

public class ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int number1=10;

		int number2= 20;
		System.out.println(number1+number2);
		
		System.out.println(number1-number2);
		
		System.out.println(number1*number2);
		
		System.out.println(number1/number2);
		
		
		
		
		
		
		
		
		System.out.println("******************");
		
		
		double number3=10;
		
		double number4=20;
		
		System.out.println(number3+number4);
		
		System.out.println(number3-number4);
		
		System.out.println(number3*number4);
		
		System.out.println(number3/number4);
		
		
		
		
		
		
		System.out.println("******************");
		
		boolean var1= true;
		
		boolean var2= false;
		
		
		
		System.out.println("***************");
		
		
		
		
		
	}

}
