package com.syntaxBatch11Class2;

public class ShortHandOperatorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int number=10;
		
		number=number+2;
		
		System.out.println(number);
		  number++;// shorter way of doing this structure number=number+1.
		
		  System.out.println(number);
		
		  System.out.println("*****************");
		  
		  number=10;
		  number=number+30; 
		  
		  System.out.println(number);
		  
		// number+=30 is for bigger number that we increment, so if we have number for increment 30 ,40, .............. then we use this form of value.
		
		number+=30;
		
		System.out.println(number);
		
		
		 
		 System.out.println("***************");
		 
		 number=10;
		 number-=5;
		 System.out.println(number);
		 
		 number=10;
		  number*=5;
		  System.out.println(number);
		  
		  number=10;
		  number/=5;
		  
		  System.out.println(number); // On shortHand Operator we can multiply, modulus and division values.
		  
		  number=10;
		  
		  number%=2;
		  
		  System.out.println(number);
	}

}
