package com.syntaxBatch11Class7;



public class DoWhileDemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=1; 
		
		while(a>=3) {
			
			System.out.println("Hi nasir");
			
			
			a++;
		}
		
		
		// whileLoop and Do whileLoop both execute the conditions when they are true but do while can execute the condition even it is not true and and it executes the blocks of code by taking one condition.
		
		int b=1;
		
		do {
			
			System.out.println("Hello nasir");
			b++;
			
		}while(b>=3); // do While Loop is better than While loop for comparing. 
		
		
		
	
		
	}

}
