package com.syntaxBatch11Class7;

public class ForLoopDemo3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		System.out.println(" I want to print numbers from 100 to 20"); // in here we do decrement
		
		for(int b=100; b>=20;    b--) {
			
			
			System.out.print(b+"  ");
		}
	}

}
