public class ForLoopDemo4EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		System.out.println(" I want to print Even numbers from 1 to 5o");
		
		for(int i=1;  i<=50;   i++) {
			
			if(i%2==0) {
			
				System.out.print(i+"  ");
				
				
			
				
				
			
		
				
			
						
						
						
						
						
						
						
			}				
					
				
		}
						
						
						
						
}}
