package com.syntaxBatch11Class7;

public class ForLoopDemo7Importantparts {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int sum=0;
		for(int i=1;  i<=6;  i++) {
			
			sum+=i;
		}
		System.out.println(sum); //if we put the Syso inside of the bracket it will  show us the final print out 6 times like 1,3,6,10,15,21.
		
		
		
		
		System.out.println("****************");// Another Example.
		
		int result=1;
		
		for(int a=0;   a<=4;  a+=2) {
			
			
			result*=a;
			
			
		}
		System.out.println(result);
	
		
	
	System.out.println("**********");// Another example.
	
	
	int result1=1;
	
	for(int b=1;  b<=4;    b+=2) {
		
		result1*=b;
		
	}
	System.out.println(result1);
	
	}
}
