package com.syntaxBatch11Class7;

public class ForLoopEvenNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(" I want to print Even numbers from 1 to 80");
		
		for( int i=1;  i<=80;    i++  ) {
		
		if(i%2==0) {
		
		System.out.print(i+"  ");
		}
		
	}
		
		System.out.println();
		System.out.println(" I want to print Odd numbers from 1 to 90");
		
		for( int a=1;  a<=90;   a++) {
			
	
		
		if( a%2==1) {
			
			System.out.print(a+"  ");
			
			
		}
}}
}