package com.syntaxBatch11Class7;

public class ForLoopMultiplicationTable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=9;
		
		for(int i=1;  i<=10;  i++) {
			
		
				
			System.out.println(num+"*"+ i+" ="+num*i);
			
		}
		
		System.out.println("******************");
		
		
		int num1=3;
		
		for(int a=1;   a<=10;   a++) {
			
			System.out.println(num1+"*"+ a+" ="+num1*a);
		}
		
		
		}
		
		
		
	
}

