public class ForLoopOddnumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		System.out.println("I want to print Odd numbers from 1 to 30");
		
		for(int i=1;  i<=30;  i+=2) {
			System.out.print(i+"  ");
		}
			
			
		
		System.out.println();
		System.out.println(" I wnat to print Odd nuber form 50 to 1");
		
		for(int a=1;  a<=50;  a+=2) {
			System.out.print(a+" ");  
		}
	}

}
