package com.syntaxBatch11Class7;

public class ForLoppDemo6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(" I want to print numbers from 1 to 200");
		
		for( int a=1;  a<=200;  a++) { // Increment
		
		System.out.print(a+"   ");
		
		}
		System.out.println();
		System.out.println(" I want to print numbers from 200 to 1");
		
		for( int b=200;  b>=1;   b--) { //decrement
		
		System.out.print(b+"  ");
		
		}
		
		
		
		
		
	}
}
