package com.syntaxBatch11Class7;

public class whileLoopDemo4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int i = 20;
		 while(i<=100) {
			 System.out.print(i+ "  ");
			 
			 i+=2;
			 
		 }
		
		
		
		 
		 
		 System.out.println(" Another way");
		 
		 
		 int a=20;
		 
		 while(a<=100) {
			 
			 if(a%2==0) {
				 
				 System.out.print(a+  "  ");
			 }
			 
			 
			 a++;
		 }
		
	}

}
