package com.syntaxBatch11Class9;

public class PrintingPatternDemo3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for( int  r=5; r>=1;  r--) {

			for( int c=4;  c>=1;  c--) {
				
				
				System.out.print( r+" ");
			}
			
			System.out.println();
		
		
		}
		
		System.out.println("****************");
		
		
		for( int  r=5; r>=1;  r--) {

			for( int c=4;  c>=1;  c--) {
				
				
				System.out.print(c+" ");
			}
			
			System.out.println();
	}
	}
}
