package com.syntaxBatch11Class9;

public class PrintingPatternsdemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for( int  r=1; r<=4;  r++) {

			for( int c=1;  c<=5;  c++) {
				
				
				System.out.print(" * ");
			}
			
			System.out.println();
		
	}
		System.out.println("***************");
		
		
		for( int  r=1; r<=10;  r++) {

			for( int c=1;  c<=10;  c++) {
				
				
				System.out.print(" * ");
			}
			
			System.out.println(); 
		}
	}
}
